      },
      "system": "http://oval.mitre.org/XMLSchema/oval-definitions-5"
    },
    "id": "xccdf_mil.disa.stig_rule_SV-230291r743957_rule",
    "Id": "xccdf_mil.disa.stig_rule_SV-230291r743957_rule",
    "weight": "10.0",
    "severity": "medium"
  },
  "id": "xccdf_mil.disa.stig_group_V-230291",
