    "ident": {
      "text": "CCI-000198",
      "system": "http://cyber.mil/cci"
    },
    "fixtext": {
      "text": "Configure non-compliant accounts to enforce a 24 hours/1 day minimum password lifetime:\n\n$ sudo chage -m 1 [user]",
